    10→function Dashboard() {
    11→  const [selectedPair, setSelectedPair] = useState(null);
    12→  const [selectedGran, setSelectedGran] = useState(null);
    13→  const [technicalsData, setTechnicalsData] = useState(null);
    14→  const [priceData, setPriceData] = useState(null);
    15→  const [selectedCount, setSelectedCount] = useState(COUNTS[0].value);
    16→  const [options, setOptions] = useState(null);
    17→  const [loading, setLoading] = useState(true);
    18→  const [loadingData, setLoadingData] = useState(false);
    19→
    20→  useEffect(() => {
    21→    loadOptions();
    22→  }, []);
    23→
