    60→            <div className="absolute inset-0 h-16 w-16 rounded-full border-4 border-primary border-t-transparent animate-spin" />
    61→          </div>
    62→          <p className="text-muted-foreground font-medium">Loading dashboard...</p>
    63→        </div>
    64→      </div>
    65→    );
    66→  }
    67→
    68→  return (
    69→    <div className="py-8 space-y-6 animate-fade-in">
