    71→      <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
    72→        <div className="space-y-1">
    73→          <h1 className="text-h2 text-foreground">Trading Dashboard</h1>
    74→          <p className="text-muted-foreground">
    75→            Analyze currency pairs with technical indicators and live charts
    76→          </p>
    77→        </div>
    78→      </div>
    79→
    80→      {/* Controls Section */}
    81→      <div className="card p-6">
    82→        <div className="flex flex-col md:flex-row items-start md:items-end gap-4 flex-wrap">
    83→          {/* Pair & Granularity Selectors */}
    84→          <div className="flex flex-wrap items-end gap-4">
    85→            <Select 
    86→              name="Currency"
    87→              title="Currency Pair"
    88→              options={options.pairs}
    89→              defaultValue={selectedPair}
    90→              onSelected={setSelectedPair}
    91→              className="w-40"
    92→            />
    93→            <Select 
    94→              name="Granularity"
    95→              title="Timeframe"
    96→              options={options.granularities}
    97→              defaultValue={selectedGran}
    98→              onSelected={setSelectedGran}
    99→              className="w-32"
   100→            />
   101→          </div>
   102→          
   103→          {/* Load Button */}
   104→          <Button 
   105→            text={loadingData ? "Loading..." : "Load Data"}
   106→            handleClick={() => loadTechnicals()}
   107→            disabled={loadingData}
   108→            icon={loadingData ? RefreshCw : Play}
   109→            className={loadingData ? "[&_svg]:animate-spin" : ""}
   110→          />
   111→        </div>
   112→        
   113→        {/* Selected Info Badge */}
   114→        {selectedPair && selectedGran && (
   115→          <div className="mt-4 pt-4 border-t border-border">
   116→            <div className="flex items-center gap-2 text-sm text-muted-foreground">
   117→              <BarChart3 className="h-4 w-4" />
   118→              <span>
   119→                Analyzing <span className="font-semibold text-foreground">{selectedPair}</span> on{' '}
   120→                <span className="font-semibold text-foreground">{selectedGran}</span> timeframe
   121→              </span>
   122→            </div>
   123→          </div>
   124→        )}
   125→      </div>
   126→
   127→      {/* Main Content */}
   128→      {(technicalsData || priceData) ? (
   129→        <div className="grid grid-cols-1 xl:grid-cols-3 gap-6">
   130→          {/* Technicals - Sidebar on large screens */}
   131→          {technicalsData && (
