   168→              <div className="flex items-center gap-2">
   169→                <span className="h-2 w-2 rounded-full bg-primary" />
   170→                Technical indicators
   171→              </div>
   172→            </div>
   173→          </div>
   174→        </div>
   175→      )}
   176→    </div>
   177→  );
   178→}
